package dynaimg_test

import (
	"github.com/reiver/go-dynaimg"

	"bytes"
	"encoding/base64"
	"fmt"
	"image/png"
	"math/rand"
	"strings"
)

func ExampleRGBA() {

	// You can pick whatever ‘width’ & ‘height’ you want.
	//
	// For the sake of this example, we have picked these.
	const width  = 50
	const height = 100

	// THIS CANNOT CHANGE!
	//
	// The ‘depth’ for this is always 4.
	//
	// The ‘depth’ is the number of bytes per pixel.
	//
	// Since each pixel is an RGBA color value, and each color channel
	// is 1 byte, since there are 4 color channels the ‘depth’ is
	// 1 × 4 = 4.
	const depth  = 4

	// This is the array whre the image data is stored.
	//
	// Note that the size of this array is a function of the
	// ‘width’, ‘height’, and ‘depth’.
	var memory [width*height*depth]uint8

	// Here is where we set up the image.
	var img dynaimg.RGBA = dynaimg.RGBA{
		Pix: memory[:],

		Width:  width,
		Height: height,
	}

	// Now we will just do something with the image.
	//
	// For the sake of our example, we will encode the image into a PNG,
	// and then serialize it as a text string.
	//
	// You do not have to do this too! You will probably do something different!
	{
		// We explicitly pick the seed so that the pseudo-random numbers
		// generated are always the same when this example runs.
		const seed = 7

		randomness := rand.New( rand.NewSource( seed ) )

		// We choose random color values for each pixel.
		for offset:=0; offset<len(memory); offset++ {
			switch offset%4 {
			case 3:
				memory[offset] = 255
			default:
				memory[offset] = uint8(randomness.Intn(255))
			}
		}
	}
	{
		var buffer strings.Builder

		buffer.WriteString("IMAGE:")

		{
			var pngBuffer bytes.Buffer

			err := png.Encode(&pngBuffer, img)
			if nil != err {
				fmt.Sprintf("ERROR:%s", err)
				return
			}

			encoded := base64.StdEncoding.EncodeToString(pngBuffer.Bytes())

			buffer.WriteString(encoded)
		}

		fmt.Println(buffer.String())
	}

	// Output:
	//
	// IMAGE:
}
